const btnSubmit=document.getElementById("submit");btnSubmit.addEventListener("click",submit);function submit(){let http=new XMLHttpRequest();http.open("POST","/sendmessage",true);http.setRequestHeader("Content-type","application/x-www-form-urlencoded");let name=document.getElementById("name");let telephone=document.getElementById("telephone");let email=document.getElementById("email");let message=document.getElementById("message");let honeypot=document.getElementById("address");if(honeypot.value.length>0){alert("Stop mucking about!");return}let valid=validate(name,telephone,email,message);if(!valid){return}btnSubmit.classList.remove("show");btnSubmit.classList.add("hide");let spinner=document.getElementById("spinner");spinner.classList.add("show");spinner.classList.remove("hide");let params="name="+name.value+"&telephone="+telephone.value+"&email="+email.value+"&message="+message.value+"&honeypot="+honeypot.value;http.send(params);http.onload=function(){spinner.classList.remove("show");spinner.classList.add("hide");let tick=document.getElementById("tick");tick.classList.add("show");tick.classList.remove("hide")}}function validate(c,a,b,d){let regexName=/^[a-zA-Z\\s]+/;let regexPhone=/[0-9]{11}/;let regexEmail=/^\S+@\S+$/;let valid=true;let value=c.value;if(value==null||value===""){notValid(c);valid=false}else{if(!regexName.test(value)){notValid(c);valid=false}else{isValid(c)}}value=a.value;if(value==null||value===""){notValid(a);valid=false}else{if(!regexPhone.test(value)){notValid(a);valid=false}else{isValid(a)}}value=b.value;if(value==null||value===""){notValid(b);valid=true}else{if(!regexEmail.test(value)){notValid(b);valid=false}else{isValid(b)}}value=d.value;if(value==null||value===""){notValid(d);valid=false}else{isValid(d)}return valid}function isValid(a){a.parentElement.classList.remove("danger")}function notValid(a){a.parentElement.classList.add("danger")};